  return (
    <div>
        <div>
            <h1>Image Annotation Page</h1>
            <h3>Upload the Image you want to anonotate and measure</h3>
            <h4>Steps to annotate you images</h4>
            <p>
              1. Upload an image by choosing a file by clicking on + button
            </p>
            <p>
              2. Go to the Gallery Page to see the uploaded images and select one image which you want to annotate
            </p>
            <p>
              3. Place the keys and save them, click on any two key points and find the distance between them in Pixels
            </p>
            <input
                accept="image/*"
                type="file"
                onChange={convertToBase64}
            />
            {image === "" || image == null ? "" : <img alt="UploadedImage" width={300} height={300} src={image} />}
            <div>
            <button onClick={() => {uploadImage(); alert("Image Uploaded! Too see images Click on Images Page")}}>Upload </button>


            </div>



      </div>

    </div>

  );
}

export default UploadPage;
