return (
  <div>
    <h1>Image Display</h1>
    {imageIds.map((imageId) => (
      <img 
        key={imageId}
        src={`/api/images/${imageId}`}
        alt="Ie"
        onClick={() => handleImageClick(imageId)}
      />
    ))}
    {selectedImageId && <p>Selected Image ID: {selectedImageId}</p>}
  </div>
);
};



export default ImagesPage;
