  });
}


  return (
    <div>
        <div>
            Upload Image
            <input
                accept="image/*"
                type="file"
                onChange={convertToBase64}
            />
            {image === "" || image == null ? "" : <img alt="UploadedImage" width={100} height={100} src={image} />}
            <div>
            <button onClick={uploadImage()} disabled = {isDisabled} >Upload </button>
            if({isDisabled}){
                <p>Hi</p>
            }

            </div>



      </div>

    </div>

  );
}

export default UploadPage;
