import React, { useEffect, useRef, useState } from 'react';
import { useParams } from 'react-router-dom';

function ImagesAnnotationPage() {
  const { id } = useParams();
  const [image, setImage] = useState(null);
  const canvasRef = useRef(null);
  const [keypoints, setKeypoints] = useState([]);
  const [selectedKeypoints, setSelectedKeypoints] = useState([]);
  const [lineDistance, setLineDistance] = useState(0);

  const drawImage = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    const img = new Image();
    img.onload = () => {
      context.drawImage(img, 0, 0, canvas.width, canvas.height);
      drawKeypoints();
      drawSelectedKeypoints();
      drawLine();
    };
    img.src = image.image;
  };

  const drawKeypoints = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    context.fillStyle = 'red';
    keypoints.forEach((keypoint) => {
      context.beginPath();
      context.arc(keypoint.x, keypoint.y, 5, 0, 2 * Math.PI);
      context.fill();
    });
  };

  const drawSelectedKeypoints = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    context.fillStyle = 'green';
    selectedKeypoints.forEach((keypoint) => {
      context.beginPath();
      context.arc(keypoint.x, keypoint.y, 5, 0, 2 * Math.PI);
      context.fill();
    });
  };

  const drawLine = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    context.strokeStyle = 'blue';
    if (selectedKeypoints.length === 2) {
      const [kp1, kp2] = selectedKeypoints;
      context.beginPath();
      context.moveTo(kp1.x, kp1.y);
      context.lineTo(kp2.x, kp2.y);
      context.stroke();
    }
  };

  const handleCanvasClick = (event) => {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;
    const newKeypoint = { x, y };
    setKeypoints((prevKeypoints) => [...prevKeypoints, newKeypoint]);
  };

  const handleKeypointClick = (keypoint) => {
    if (selectedKeypoints.includes(keypoint)) {
      setSelectedKeypoints(selectedKeypoints.filter((kp) => kp !== keypoint));
    } else {
      setSelectedKeypoints([...selectedKeypoints, keypoint]);
    }
  };

  useEffect(() => {
    fetch(`http://localhost:5000/images/edit/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setImage(data.data);
      })
      .catch((error) => {
        console.log("Error fetching image:", error);
      });
  }, [id]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    context.clearRect(0, 0, canvas.width, canvas.height);
    drawImage();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [image]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    context.clearRect(0, 0, canvas.width, canvas.height);
    drawKeypoints();
    drawSelectedKeypoints();
    drawLine();

    // Calculate distance between selected keypoints
    if (selectedKeypoints.length === 2) {
      const [kp1, kp2] = selectedKeypoints;
      const distance = Math.sqrt(
        Math.pow(kp2.x - kp1.x, 2) + Math.pow(kp2.y - kp1.y, 2)
      );
      setLineDistance(distance);
    } else {
      setLineDistance(0);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [keypoints, selectedKeypoints]);

  return (
    <div>
      {image && (
        <img alt="Clicked_Image" width={600} height={600} src={image.image} />
      )}
      <canvas
        ref={canvasRef}
        width={600}
        height={600}
        onClick={handleCanvasClick}
      />
      <div>
        <h4>Keypoints:</h4>
        {keypoints.map((keypoint, index) => (
          <div key={index} onClick={() => handleKeypointClick(keypoint)}>
            {`Keypoint ${index + 1}: (${keypoint.x}, ${keypoint.y})`}
          </div>
        ))}
      </div>
      <div>
        {selectedKeypoints.length === 2 && (
          <div>
            <h4>Distance:</h4>
            {`Distance between keypoints: ${lineDistance.toFixed(2)} pixels`}
          </div>
        )}
      </div>
    </div>
  );
}

export default ImagesAnnotationPage;
