import React, {useState } from 'react';

function UploadPage(props) {
  const [image,setImage] = useState("");
  const [uploadStatus, setUploadStatus] = useState(null);

  function convertToBase64(e) {
    console.log(e);
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () => {
        console.log(reader.result); //base64encoded string  
        setImage(reader.result);
    };
    reader.onerror = error => {
        console.log("Error: ", error);
        console.log("This is Base64 error: ", error);
    };
}


function uploadImage() {
  fetch("http://localhost:5000/uploadImage", {
      method: "POST",
      crossDomain: true,
      headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
          "Access-Control-Allow-Origin": "*",
      },
      body: JSON.stringify({
          base64: image
      })
  }).then((res) => res.json()).then((data) =>{

    setUploadStatus("success");

  })


}

  return (
    <div>
        <div>
            Let's Upload Images
            <input
                accept="image/*"
                type="file"
                onChange={convertToBase64}
            />
            {image === "" || image == null ? "" : <img alt = "UploadedImage" width={100} height={100} src={image} />}
            <div>
            <button onClick={uploadImage}>Upload</button>
            </div>
            {uploadStatus === "success" && <p>Image uploaded successfully!</p>}
        {uploadStatus === "failure" && <p>Failed to upload image. Please try again.</p>}

            </div>
    </div>
  );
}

export default UploadPage;
